<style type="text/scss">
    @import '../variables.scss';
    * {
        box-sizing: border-box;
    }
    .game__quote {
        width: 100%;
        padding: 0 0 80px;
        &-text {
            position: relative;
            border: 2px solid;
            border-bottom: none;
            font: 18px 'Oswald', sans-serif;
            text-transform: uppercase;
            &:before {
                content: '';
                position: absolute;
                left: 0;
                bottom: 0;
                right: 251px;
                border-bottom: 2px solid;
            }
            &:after {
                content: '';
                position: absolute;
                right: 0;
                top: calc(100% - 3px);
                width: 252px;
                height: 42px;
                background: url('/dist/images/quote-triangle.png') no-repeat 0 0;
            }
            &-inner {
                position: relative;
                display: flex;
                justify-content: center;
                align-items: center;
                min-height: 202px;
                padding: 30px 20px;
                &:before {
                    content: 'â€œ';
                    position: absolute;
                    left: 25px;
                    top: 15px;
                    font: 900 41px/1 "TT Norms",sans-serif;
                }
            }
        }
        &-photo {
            position: absolute;
            right: 0;
            bottom: 0;
            width: 185px;
            height: 181px;
            background: url('/dist/images/quote-photo.png') no-repeat 0 0;
        }
    }
    .game__answers {
        display: flex;
        flex-direction: column;
        align-items: center;
    }
    .game__answer {
        min-width: calc(100% - 240px);
        margin-bottom: 15px;
        text-align: center;
        text-transform: none;
        &:last-child {
            margin: 0;
        }
        .game__question_answered & {
            background: transparent;
            color: map-get($colors, 'secondary');
            opacity: .5;
            cursor: default;
        }
        .game__question_answered &_correct,
        .game__question_answered &_incorrect {
            opacity: 1;
            color: inherit;
        }
        .game__question_answered &_correct {
            background: linear-gradient(to right, #49cf50, #22be2a);
        }
        .game__question_answered &_incorrect {
            background: linear-gradient(to right, #F36F49, #E74969);
            animation-duration: .7s;
            animation-fill-mode: both;
            animation-name: flash;
        }

    }
    .game__question-comment {
        position: absolute;
        left: 0;
        top: 0;
        width: 100%;
        height: 100%;
        background-repeat: no-repeat;
        background-position: 50% 50%;
        background-size: cover;
        &:after {
            content: '';
            position: absolute;
            display:block;
            width: 100%;
            height: 128px;
            background: linear-gradient(0deg, rgba(0,0,0,0) 0%, rgba(0,0,0,.75) 100%);
        }
        &:before {
            content: '';
            position: absolute;
            top: 8px;
            right: 8px;
            display: block;
            width: 96px;
            height: 80px;
